      @direction = TURN[direction]
    else
      self.location = next_location
    end
  end

  def to_s
    {location:, direction:, previous_locations:}.to_s
  end

  private

  attr_reader :row_size
  attr_writer :previous_locations

  def up = location - row_size

  def right = location + 1

  def down = location + row_size

  def left = location - 1

  def hashed_state(location, direction) = location.to_s + direction.to_s
end

data = DATA.read.chars
row_size = data.index("\n")
map = data.reject { |d| d == "\n" }

DIRECTION = {
  "^" => :up,
  ">" => :right,
  "V" => :down,
  "<" => :left
}

original_location = nil
original_direction = nil
DIRECTION.keys.each do |cell|
  if (loc = map.index(cell))
    original_location = loc
    original_direction = DIRECTION[cell]
  end
end

loops = []
OBSTACLE_CANDIDATES.each do |candidate_location|
  next if candidate_location == original_location

  candidate_map = map.dup
  candidate_map[candidate_location] = Guard::OBSTACLE

  guard = Guard.new(
    map: candidate_map,
    row_size: row_size,
    location: original_location,
    direction: original_direction
  )

  while guard.step
  end

  loops << candidate_location if guard.trapped
end

puts loops.size

